import React, { useState,useEffect} from 'react'
import axios from 'axios'

const Lecture = () => {
    const [course, setcourse]=useState() 
    const [inst, setinst]=useState() 
  const [Data, setData] = useState({
    courseName: '', instructorName: '', Date: ''
  })
  const getData = (ev) => {
    let name = ev.target.name
    let value = ev.target.value
    setData({ ...Data, [name]: value })
  }
  const submit = (ev) => {
    let val = {
    'instructorName': Data.instructorName,
    'courseName': Data.courseName,
    'Date': Data.Date,
  }
    axios.post(`${process.env.REACT_APP_BACKEND}/addlecture`, val).then(() => {
      alert("data added")
    }).catch((e) => { console.log(e) })
  }
  return (
    <>
      <div className='Main'>
        <div className='Course'>
        <h1 style={{ 'textAlign': 'center', 'marginTop': '1rem' }}>SCHEDULE LECTURE</h1>
          <div>
            <form>
              <label>Name</label>
              <select name="courseName" onChange={getData}>

              </select>
              <label className='mt-2'>Level</label>
              <input type="text" name="instructorName" onChange={getData} value={Data.instructorName}></input>
              <label>Description</label>
              <input type="date" name="Date" onChange={getData} value={Data.Date}></input>

              <button onClick={submit}>Add Course</button>
            </form>
          </div>

        </div>
      </div>
    </>
  )
}

export default Lecture
