import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Lecture = () => {
  const [course, setcourse] = useState([])
  const [inst, setinst] = useState([])
  const [Data, setData] = useState({
    courseName: '', instructorName: '', Date: ''
  })
  useEffect(() => {
    axios.get(`${process.env.REACT_APP_BACKEND}/getinst`).then((res) => {
      setcourse(res.data)
    }).catch((e) => { console.log(e) })
    axios.get(`${process.env.REACT_APP_BACKEND}/getcourse`).then((res) => {
      setinst(res.data)
    }).catch((e) => { console.log(e) })
  }, [])
  const getData = (ev) => {
    let name = ev.target.name
    let value = ev.target.value
    setData({ ...Data, [name]: value })
  }
  const submit = (ev) => {
    ev.preventDefault()
    let val = {
      'instructorName': Data.instructorName,
      'courseName': Data.courseName,
      'Date': Data.Date,
    }
    axios.post(`${process.env.REACT_APP_BACKEND}/addlecture`, val).then((res) => {
      alert(res.data)
      setData({
        courseName:'choose', instructorName:'choose', Date:''
      })
    }).catch((e) => { console.log(e) })
  }
  return (
    <>
      <div className='Main'>
        <div className='Course'>
          <h1 style={{ 'textAlign': 'center', 'marginTop': '1rem' }}>SCHEDULE LECTURE</h1>
          <div>
            <form>
              <label>Course Name</label>
              <select name="courseName" onChange={getData} value={Data.courseName} >
                <option>choose</option>
                {
                  course.map((val) => {
                    return (
                      <option value={val.Name} key={val._id}>{val.Name}</option>
                    )
                  })
                }
              </select>
              <label className='mt-2'>Instructor Name</label>

              <select name="instructorName" onChange={getData}>
                <option>choose</option>
                {
                  inst.map((val) => {
                    return (
                      <option value={val.Name} key={val._id}>{val.Name}</option>
                    )
                  })
                }
              </select>
              <label>Date</label>
              <input type="date" name="Date" onChange={getData} value={Data.Date}></input>
              <button onClick={submit}>Add Course</button>
            </form>
          </div>
        </div>
      </div>
    </>
  )
}

export default Lecture
