import React, { useState } from 'react'
import axios from 'axios'

const AddCourse = () => {
  const [Data, setData] = useState({
    Name: '', Level: '', Description: ''
  })
  const [Image, setImage] = useState()
  const getData = (ev) => {
    let name=ev.target.name
    let value=ev.target.value
    setData({...Data,[name]:value})
  }
  const submit=()=>{
    let val=new FormData()
    val.append('Name',Data.Name)
    val.append('Level',Data.Level)
    val.append('Description',Data.Description)
    val.append('Image',Image)
       axios.post(`${process.env.BACKEND}/addcourse`,val).then(()=>{
        alert("data added")
       }).catch((e)=>{console.log(e)})
  }
  return (
    <>
      <div className='Main'>
      <h1 style={{'textAlign':'center'}}>ADD COURSE</h1>
        <div className='Course'>
          <form>
            <div>
              <label>Name</label>
              <input type="text" name="Name" onChange={getData} value={Data.Name}></input>
              <label className='mt-2'>Level</label>
              <input type="number" name="Level" onChange={getData} value={Data.Level}></input>
              <label>Description</label>
              <input type="text" name="Description" onChange={getData} value={Data.Description}></input>
              <label className='mt-2'>Image</label>
              <input type="file" name="Image" onChange={(ev) => { setImage(ev.target.files[0]) }} className='form-control'></input>
              <button onClick={submit}>Add Course</button>
            </div>
          </form>
        </div>
      </div>
    </>
  )
}

export default AddCourse
