              <input type="file" name="Image" onChange={(ev) => { setImage(ev.target.files[0]) }} className='form-control'></input>
              <button onClick={submit}>Add Course</button>
            </div>
          </form>
        </div>
      </div>
    </>
  )
}

export default AddCourse
